package Loop_statements;

public class For_each_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String []arr= {"one","two","three","four"};
		int []num= {1,2,3,4,5};
		for(int num1:num) {
			System.out.println(num1);
		}
for(String str:arr) {
			System.out.println(str);

}
	}

}
