package Operators;

public class Logical_and_Bitwise_Opertor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=30;
// AND
		System.out.println(a<b&& b>c);//false
		System.out.println(a<b && b<c);//true




// In "OR" 
		System.out.println(a<b || b>c);//true
		System.out.println(a>b || b>c);


	}

}
