package Operators;

public class UnaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		System.out.println(a);
System.out.println(a++);
		System.out.println(a);
		System.out.println(a++);
		System.out.println(a);




	//Decrementing Operator
		System.out.println("Decrementing the values");
		int b=20;
		System.out.println(b);
		System.out.println(b--);
		System.out.println(b);




	}

}
